  #{isTop,jdbcType=INTEGER}, #{addName,jdbcType=VARCHAR}, #{articleContent,jdbcType=LONGVARCHAR}
  )

mapper.update:
update ssm_article
set article_title = #{articleTitle,jdbcType=VARCHAR},
  article_create_date = #{articleCreateDate,jdbcType=VARCHAR},
  is_top = #{isTop,jdbcType=INTEGER},
  add_name = #{addName,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id,article_title,article_create_date,article_content,add_name from ssm_article


    and article_title like #{articleTitle}



  limit #{start},#{size}

